<span class="token keyword">type</span> <span class="token class-name">PixltonianItem</span> <span class="token operator">=</span> PixltonianCustomItem <span class="token operator">|</span> PixltonianAutoItem<span class="token punctuation">;</span>

<span class="token keyword">interface</span> <span class="token class-name">PixltonianItemBase</span> <span class="token punctuation">{</span>
