<span class="token keyword">interface</span> <span class="token class-name">PixltonianCustomItem</span> <span class="token keyword">extends</span> <span class="token class-name">PixltonianItemBase</span> <span class="token punctuation">{</span>
    <span class="token comment">// A type of content posted by persona itself</span>
    id<span class="token operator">:</span> PixltonianCustomItemID<span class="token punctuation">;</span> <span class="token comment">// `${PixltonianID}-item-custom-${index}`</span>
    tags<span class="token operator">?</span><span class="token operator">:</span> <span class="token builtin">string</span><span class="token punctuation">[</span><span class="token punctuation">]</span><span class="token punctuation">;</span>
