        id<span class="token operator">:</span> <span class="token builtin">string</span><span class="token punctuation">;</span> <span class="token comment">// Link id for the non-original item, for example: comment like</span>
        target<span class="token operator">:</span> PixltonianCustomItemID <span class="token operator">|</span> PixltonianAutoItemID<span class="token punctuation">;</span> <span class="token comment">// Target of the non-original item</span>
    <span class="token punctuation">}</span><span class="token punctuation">;</span>

    contents<span class="token operator">?</span><span class="token operator">:</span> <span class="token punctuation">{</span>
        <span class="token comment">// Contents of current item, possibly multiple different types of content</span>
        tags<span class="token operator">?</span><span class="token operator">:</span> <span class="token builtin">string</span><span class="token punctuation">[</span><span class="token punctuation">]</span><span class="token punctuation">;</span>
        address<span class="token operator">:</span> ThirdPartyAddress<span class="token punctuation">;</span>
